import DeleteButton from "./DeleteButton";


export const Item = ({task, deleteItem}) => {
    console.log(task)
    return (
       <div className="item">
       <p>{task.task}</p> 
         <DeleteButton color='red' text='Delete' onClick={deleteItem} taskId={task.id}/>
        </div>
    );
}
