import React from "react";

export const Item = ({task, 
    deleteItem}) => {
    return(
       <div className="item-box"> 
       <div className="item">
       <p>{task.task}</p>
         <button onClick={() => deleteItem(task.id)} className="delete-button">Delete</button>
        </div>
        </div>
    )
}
