import React, { useState, useEffect } from "react";
import { MyForm } from "./MyForm.jsx";
import { v4 as uuidv4 } from "uuid";
import { Item } from "./Item.jsx";
uuidv4();

export const ParentForm = () => {
  let hasAdded = false;

  const [items, setItems] = useState(() => {
    const storedTasks = localStorage.getItem("tasks");
    return storedTasks ? JSON.parse(storedTasks) : [];
  });

  const addItem = (item) => {
    hasAdded = true;
    if (item !== "") {
      const newItems = [...items, { id: uuidv4(), task: item }];
      setItems(newItems);
      localStorage.setItem("tasks", JSON.stringify(newItems));
    }
  };

  const deleteItem = (id) => {
    const newItems = items.filter((item) => item.id !== id);
    setItems(newItems);
    localStorage.setItem("tasks", JSON.stringify(newItems));
    if (newItems.length === 0) {
      hasAdded = false;
    }
  };

  const clearTasks = () => {
    setItems([]);
    localStorage.removeItem("tasks");
    hasAdded = false;
  };

  return (
    <div className="parentForm">
    <MyForm addItem={addItem}/>
    {items.length > 1 && (
        <button onClick={clearTasks}>Clear All Tasks at ones</button>
    )}
    {items.length > 0 ? (
         items.map((item, index) => (
            <div className="myItem" key={index}>
                <Item task={item} deleteItem={deleteItem}/>
            </div>
         )) 
    ) : (
   <p>Your task field is empty.</p>
 )} 
    {items.length === 0 && h && (
        <p>Your task field is empty.</p>
    )}
</div>
  );
};
export default ParentForm;
