import React, { useState } from "react";



const tasks = [
    {
        id: 1, 
        text: 'Shopping',
        day : '5th May',
        reminder: true,
    },
    {
        id: 2, 
        text: 'Going out',
        day : '6th May',
        reminder: true,
    },
    {
        id: 3, 
        text: 'Studying',
        day : '7th May',
        reminder: false,
    },
];

const Tasks = () => {
    return (
    <>
    {tasks.map((task)=>(

    <h3 key={task.id}>{task.text}</h3>


))}

    </>
    ); 
};

export default Tasks;
