import { useEffect, useState } from "react";
import Button from "./Button";


export const MyForm = ({addItem}) => {
    const [value, setValue] = useState("")
    const myWin = window.localStorage

    const handleSubmit = e => {
        e.preventDefault();
        myWin.clear();
        addItem(value);
        setValue ("")
    }
    useEffect(() => {
        myWin.setItem('task', value)
    }, [value])


    return( 
    <div>
        <form className='My-form' onSubmit={handleSubmit}><h1>Write your tasks here</h1> <br />
            <input type='text' className='My-input' value={value} placeholder='Write your task here...' onChange={(e) => setValue(e.target.value)}/>
       <Button  color="blue" text="Add"/>
        </form>
    </div>
    )
}
